package utp.ac.pa.sistema.domain;import java.time.LocalDate;public class Pago {public String id,contratoId;public LocalDate fechaPago;public double monto;public String metodo;public Pago(String id,String contratoId,LocalDate fechaPago,double monto,String metodo){this.id=id;this.contratoId=contratoId;this.fechaPago=fechaPago;this.monto=monto;this.metodo=metodo;}public String toString(){return "Pago{"+id+", $"+monto+", "+fechaPago+", "+metodo+"}";}}