package utp.ac.pa.sistema.domain;import java.time.LocalDate;public class SolicitudMantenimiento {public enum Estado{REGISTRADA,EN_PROCESO,RESUELTA}public String id;public Propiedad propiedad;public Inquilino solicitadoPor;public String descripcion;public LocalDate fechaSolicitud=LocalDate.now();public Estado estado=Estado.REGISTRADA;public Tecnico asignado;public SolicitudMantenimiento(String id,Propiedad propiedad,Inquilino solicitadoPor,String descripcion){this.id=id;this.propiedad=propiedad;this.solicitadoPor=solicitadoPor;this.descripcion=descripcion;}public void asignarTecnico(Tecnico t){this.asignado=t;this.estado=Estado.EN_PROCESO;}public void marcarResuelta(){this.estado=Estado.RESUELTA;}public String toString(){return "Ticket{"+id+", prop="+propiedad.id+", por="+solicitadoPor.nombreUsuario+", estado="+estado+"}";}}