package utp.ac.pa.sistema.domain;import java.time.LocalDate;import java.util.*;public class ContratoAlquiler {public enum Estado{ACTIVO,FINALIZADO,CANCELADO}public String id;public Propiedad propiedad;public Inquilino inquilino;public LocalDate fechaInicio,fechaFin;public double montoMensual;public Estado estado=Estado.ACTIVO;public java.util.List<Pago> pagos=new ArrayList<>();public ContratoAlquiler(String id,Propiedad propiedad,Inquilino inquilino,LocalDate fechaInicio,LocalDate fechaFin,double montoMensual){this.id=id;this.propiedad=propiedad;this.inquilino=inquilino;this.fechaInicio=fechaInicio;this.fechaFin=fechaFin;this.montoMensual=montoMensual;}public void registrarPago(Pago p){pagos.add(p);}public boolean estaVigente(LocalDate hoy){return(estado==Estado.ACTIVO)&&!hoy.isAfter(fechaFin);}public String toString(){return "Contrato{"+id+", prop="+propiedad.id+", inq="+inquilino.nombreUsuario+"}";}}