package utp.ac.pa.sistema.domain;import java.time.LocalDateTime;public class Notificacion {public String id,usuarioId,mensaje;public LocalDateTime fecha=LocalDateTime.now();public boolean leida=false;public Notificacion(String id,String usuarioId,String mensaje){this.id=id;this.usuarioId=usuarioId;this.mensaje=mensaje;}public void marcarLeida(){this.leida=true;}public String toString(){return "Notificacion{"+mensaje+", para="+usuarioId+"}";}}