package utp.ac.pa.sistema.domain;import java.time.LocalDate;public class PlanPago {public enum Estado{PENDIENTE,VENCIDO,PAUSADO}public String contratoId;public LocalDate proximoVencimiento;public int diaCorte;public Estado estado=Estado.PENDIENTE;public PlanPago(String contratoId,int diaCorte){this.contratoId=contratoId;this.diaCorte=diaCorte;}public void programar(LocalDate proximo){this.proximoVencimiento=proximo;}public String toString(){return "PlanPago{contrato="+contratoId+", vence="+proximoVencimiento+", diaCorte="+diaCorte+"}";}}