package utp.ac.pa.sistema.domain;import java.util.*;public class Propiedad {public enum Tipo{CASA,APARTAMENTO,LOCAL,OFICINA}public String id;public Tipo tipo;public double areaM2;public int habitaciones;public int banos;public boolean amoblado;public Direccion direccion;public java.util.List<ServicioIncluido> servicios=new ArrayList<>();public Propiedad(String id,Tipo tipo,Direccion dir){this.id=id;this.tipo=tipo;this.direccion=dir;}public void agregarServicio(ServicioIncluido s){servicios.add(s);}public String toString(){return "Propiedad{"+id+", "+tipo+", dir="+direccion+"}";}}